import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useFormik } from 'formik';
import * as yup from 'yup';
import {
  Container,
  Paper,
  Typography,
  TextField,
  Button,
  Box,
  Grid,
} from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import axios from 'axios';

const validationSchema = yup.object({
  title: yup
    .string()
    .required('Title is required')
    .min(2, 'Title should be at least 2 characters'),
  author: yup
    .string()
    .required('Author is required')
    .min(2, 'Author name should be at least 2 characters'),
  publisher: yup
    .string()
    .required('Publisher is required'),
  publishedDate: yup
    .date()
    .required('Published date is required')
    .max(new Date(), 'Published date cannot be in the future'),
  overview: yup
    .string()
    .required('Overview is required')
    .min(10, 'Overview should be at least 10 characters'),
  isbn: yup
    .string()
    .required('ISBN is required')
    .matches(/^(?:\d[- ]?){9}[\dX]$/, 'Invalid ISBN format'),
  price: yup
    .number()
    .required('Price is required')
    .positive('Price must be positive')
    .min(0, 'Price cannot be negative'),
  quantity: yup
    .number()
    .required('Quantity is required')
    .integer('Quantity must be a whole number')
    .min(0, 'Quantity cannot be negative'),
});

const EditBook = () => {
  const [book, setBook] = useState(null);
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    fetchBook();
  }, [id]);

  const fetchBook = async () => {
    try {
      const response = await axios.get(`https://api.example.com/books/${id}`); // Replace with your API endpoint
      setBook(response.data);
      formik.setValues(response.data);
    } catch (error) {
      console.error('Error fetching book:', error);
    }
  };

  const formik = useFormik({
    initialValues: {
      title: '',
      author: '',
      publisher: '',
      publishedDate: '',
      overview: '',
      isbn: '',
      price: '',
      quantity: '',
    },
    validationSchema: validationSchema,
    onSubmit: async (values) => {
      try {
        await axios.put(`https://api.example.com/books/${id}`, values); // Replace with your API endpoint
        navigate('/');
      } catch (error) {
        console.error('Error updating book:', error);
      }
    },
  });

  if (!book) {
    return (
      <Container maxWidth="md" sx={{ mt: 4 }}>
        <Typography>Loading...</Typography>
      </Container>
    );
  }

  return (
    <Container maxWidth="md" sx={{ mt: 4, mb: 4 }}>
      <Button
        startIcon={<ArrowBackIcon />}
        onClick={() => navigate('/')}
        sx={{ mb: 2 }}
      >
        Back to List
      </Button>
      <Paper sx={{ p: 3 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Edit Book
        </Typography>
        <Box component="form" onSubmit={formik.handleSubmit} sx={{ mt: 3 }}>
          <Grid container spacing={3}>
            <Grid item xs={12}>
              <TextField
                fullWidth
                id="title"
                name="title"
                label="Title"
                value={formik.values.title}
                onChange={formik.handleChange}
                error={formik.touched.title && Boolean(formik.errors.title)}
                helperText={formik.touched.title && formik.errors.title}
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                id="author"
                name="author"
                label="Author"
                value={formik.values.author}
                onChange={formik.handleChange}
                error={formik.touched.author && Boolean(formik.errors.author)}
                helperText={formik.touched.author && formik.errors.author}
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                id="publisher"
                name="publisher"
                label="Publisher"
                value={formik.values.publisher}
                onChange={formik.handleChange}
                error={formik.touched.publisher && Boolean(formik.errors.publisher)}
                helperText={formik.touched.publisher && formik.errors.publisher}
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                id="publishedDate"
                name="publishedDate"
                label="Published Date"
                type="date"
                value={formik.values.publishedDate}
                onChange={formik.handleChange}
                error={formik.touched.publishedDate && Boolean(formik.errors.publishedDate)}
                helperText={formik.touched.publishedDate && formik.errors.publishedDate}
                InputLabelProps={{ shrink: true }}
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                id="isbn"
                name="isbn"
                label="ISBN"
                value={formik.values.isbn}
                onChange={formik.handleChange}
                error={formik.touched.isbn && Boolean(formik.errors.isbn)}
                helperText={formik.touched.isbn && formik.errors.isbn}
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                id="price"
                name="price"
                label="Price"
                type="number"
                value={formik.values.price}
                onChange={formik.handleChange}
                error={formik.touched.price && Boolean(formik.errors.price)}
                helperText={formik.touched.price && formik.errors.price}
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                id="quantity"
                name="quantity"
                label="Quantity"
                type="number"
                value={formik.values.quantity}
                onChange={formik.handleChange}
                error={formik.touched.quantity && Boolean(formik.errors.quantity)}
                helperText={formik.touched.quantity && formik.errors.quantity}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                id="overview"
                name="overview"
                label="Overview"
                multiline
                rows={4}
                value={formik.values.overview}
                onChange={formik.handleChange}
                error={formik.touched.overview && Boolean(formik.errors.overview)}
                helperText={formik.touched.overview && formik.errors.overview}
              />
            </Grid>
            <Grid item xs={12}>
              <Button
                color="primary"
                variant="contained"
                fullWidth
                type="submit"
                size="large"
              >
                Update Book
              </Button>
            </Grid>
          </Grid>
        </Box>
      </Paper>
    </Container>
  );
};

export default EditBook;
