import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useFormik } from 'formik';
import * as yup from 'yup';
import {
  Container,
  Paper,
  TextField,
  Button,
  Typography,
  Box,
  Alert,
  Snackbar,
} from '@mui/material';
import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

const validationSchema = yup.object({
  title: yup.string().required('Title is required'),
  author: yup.string().required('Author is required'),
  publisher: yup.string().required('Publisher is required'),
  publishedDate: yup.date().required('Published date is required'),
  isbn: yup.string().required('ISBN is required'),
  price: yup.number().required('Price is required').min(0, 'Price must be positive'),
  quantity: yup.number().required('Quantity is required').min(0, 'Quantity must be positive'),
  overview: yup.string().required('Overview is required'),
});

const EditBook = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(true);

  const formik = useFormik({
    initialValues: {
      title: '',
      author: '',
      publisher: '',
      publishedDate: '',
      isbn: '',
      price: '',
      quantity: '',
      overview: '',
    },
    validationSchema: validationSchema,
    onSubmit: async (values) => {
      try {
        await axios.put(`${API_URL}/books/${id}`, values);
        setSuccess('Book updated successfully!');
        setTimeout(() => {
          navigate('/');
        }, 2000);
      } catch (error) {
        setError('Error updating book: ' + (error.response?.data?.message || error.message));
      }
    },
  });

  useEffect(() => {
    const fetchBook = async () => {
      try {
        const response = await axios.get(`${API_URL}/books/${id}`);
        const book = response.data;
        formik.setValues({
          title: book.title,
          author: book.author,
          publisher: book.publisher,
          publishedDate: new Date(book.publishedDate).toISOString().split('T')[0],
          isbn: book.isbn,
          price: book.price,
          quantity: book.quantity,
          overview: book.overview || '',
        });
        setLoading(false);
      } catch (error) {
        setError('Error fetching book: ' + (error.response?.data?.message || error.message));
        setLoading(false);
      }
    };

    fetchBook();
  }, [id]);

  if (loading) {
    return (
      <Container maxWidth="md" sx={{ mt: 4 }}>
        <Typography>Loading...</Typography>
      </Container>
    );
  }

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Paper sx={{ p: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Edit Book
        </Typography>

        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}

        <form onSubmit={formik.handleSubmit}>
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
            <TextField
              fullWidth
              id="title"
              name="title"
              label="Title"
              value={formik.values.title}
              onChange={formik.handleChange}
              error={formik.touched.title && Boolean(formik.errors.title)}
              helperText={formik.touched.title && formik.errors.title}
            />

            <TextField
              fullWidth
              id="author"
              name="author"
              label="Author"
              value={formik.values.author}
              onChange={formik.handleChange}
              error={formik.touched.author && Boolean(formik.errors.author)}
              helperText={formik.touched.author && formik.errors.author}
            />

            <TextField
              fullWidth
              id="publisher"
              name="publisher"
              label="Publisher"
              value={formik.values.publisher}
              onChange={formik.handleChange}
              error={formik.touched.publisher && Boolean(formik.errors.publisher)}
              helperText={formik.touched.publisher && formik.errors.publisher}
            />

            <TextField
              fullWidth
              id="publishedDate"
              name="publishedDate"
              label="Published Date"
              type="date"
              value={formik.values.publishedDate}
              onChange={formik.handleChange}
              error={formik.touched.publishedDate && Boolean(formik.errors.publishedDate)}
              helperText={formik.touched.publishedDate && formik.errors.publishedDate}
              InputLabelProps={{
                shrink: true,
              }}
            />

            <TextField
              fullWidth
              id="isbn"
              name="isbn"
              label="ISBN"
              value={formik.values.isbn}
              onChange={formik.handleChange}
              error={formik.touched.isbn && Boolean(formik.errors.isbn)}
              helperText={formik.touched.isbn && formik.errors.isbn}
            />

            <TextField
              fullWidth
              id="price"
              name="price"
              label="Price"
              type="number"
              value={formik.values.price}
              onChange={formik.handleChange}
              error={formik.touched.price && Boolean(formik.errors.price)}
              helperText={formik.touched.price && formik.errors.price}
            />

            <TextField
              fullWidth
              id="quantity"
              name="quantity"
              label="Quantity"
              type="number"
              value={formik.values.quantity}
              onChange={formik.handleChange}
              error={formik.touched.quantity && Boolean(formik.errors.quantity)}
              helperText={formik.touched.quantity && formik.errors.quantity}
            />

            <TextField
              fullWidth
              id="overview"
              name="overview"
              label="Book Overview"
              multiline
              rows={4}
              value={formik.values.overview}
              onChange={formik.handleChange}
              error={formik.touched.overview && Boolean(formik.errors.overview)}
              helperText={formik.touched.overview && formik.errors.overview}
            />

            <Box sx={{ display: 'flex', gap: 2, justifyContent: 'flex-end' }}>
              <Button
                variant="outlined"
                onClick={() => navigate('/')}
              >
                Cancel
              </Button>
              <Button
                variant="contained"
                type="submit"
                disabled={formik.isSubmitting}
              >
                Update Book
              </Button>
            </Box>
          </Box>
        </form>
      </Paper>

      <Snackbar
        open={!!success}
        autoHideDuration={3000}
        onClose={() => setSuccess('')}
        message={success}
      />
    </Container>
  );
};

export default EditBook;
